Here is an example C++ program that reads a text file line by line and counts the number of occurrences of a specific word in the file:

#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main() {

    ifstream file;
    string word;
    int count;

    file.open("myfile.txt");

    if (!file.is_open()) {
        cout << "Error opening file" << endl;
        return 1;
    }

    while (!file.eof()) {
        getline(file, word);
        count = 0;

        for (int i = 0; i < word.length(); i++) {
            if (word[i] =='' || word[i] == ',' || word[i] == '.' || word[i] == '!') {
                count++;
            }
        }

        cout << "Word: " << word << " Frequency: " << count << endl;
    }

    file.close();

    return 0;
}

