using System;

class Program
{
    static void Main()
    {
        int[] one = { 1, 2, 3, 4 };
        int[] two = { 1, 2, 3, 4, 5, 6 };

        // Step 1: Get the count of elements in both arrays
        int lengthOne = one.Length;
        int lengthTwo = two.Length;

        // Step 2: Initialize two HashSet collections with the two arrays as input
        HashSet<int> setOne = new HashSet<int>(one);
        HashSet<int> setTwo = new HashSet<int>(two);

        // Step 3: Find the common elements
        for (int i = 0; i < lengthOne; i++)
        {
            if (setOne.Contains(one[i]))
            {
                for (int j = 0; j < lengthTwo; j++)
                {
                    if (setTwo.Contains(two[j]))
                    {
                        Console.WriteLine("The common element is: " + one[i] + " & " + two[j]);
                    }
                }
            }
        }

        // Step 4: Print the end result
        Console.WriteLine("Common elements:");
        foreach (int element in setOne)
        {
            Console.WriteLine("- {0}", element);
        }
    }
}

