#include <math.h>
#include <stdio.h>

typedef struct {
    float x, y, z;
} point3;

typedef struct {
    float m[3][3];
} matrix3x3;

point3 normalize(point3 p) {
    float length = sqrt(p.x * p.x + p.y * p.y + p.z * p.z);
    point3 result = {p.x / length, p.y / length, p.z / length};
    return result;
}

matrix3x3 matrixFromNormal(point3 normal) {
    point3 n = normalize(normal);
    matrix3x3 m = {{{1, 0, 0}, {0, 1, 0}, {0, 0, 1}}};
    m.m[0][2] = n.x;
    m.m[1][2] = n.y;
    m.m[2][2] = n.z;
    return m;
}

